using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class CardCfg  {	public int ID { get; set; }	public string Texture { get; set; }	public string Name { get; set; }	public int CardType { get; set; }	 	public static string configName = "CardCfg";	public static CardCfg config { get; set; }	public static LanguageConfigData languageConfigData;	public string version { get; set; }	public List<CardCfg> datas { get; set; }	public static CardCfg Get(int id)	{		if (config == null)		{			config = ConfigManager.Instance.Readjson<CardCfg>(configName);			languageConfigData = ConfigManager.Instance.ReadLanguageJson(configName);;		}		foreach (var item in config.datas)		{			if (item.ID == id)			{ 					return item;			}		}		return null;	}	public static List<CardCfg> GetList()	{		if (config == null)		{			config= ConfigManager.Instance.Readjson<CardCfg>(configName);		}		return config.datas;	}	public static string GetLangugeText(int id, Language language)	{		if (config == null)		{			config = ConfigManager.Instance.Readjson<CardCfg>(configName);			languageConfigData = ConfigManager.Instance.ReadLanguageJson(configName);;		}		if (languageConfigData.languageItemDatas.ContainsKey(id))		{			foreach (var data in languageConfigData.languageItemDatas[id])			{				if (data.language == language)				return data.value;			}		}		return "";	}	}