using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class DialogCfg  {	public int ID { get; set; }	public int Character { get; set; }	public int GroupID { get; set; }	public string Content { get; set; }	 	public static string configName = "DialogCfg";	public static DialogCfg config { get; set; }	public static LanguageConfigData languageConfigData;	public string version { get; set; }	public List<DialogCfg> datas { get; set; }	public static DialogCfg Get(int id)	{		if (config == null)		{			config = ConfigManager.Instance.Readjson<DialogCfg>(configName);			languageConfigData = ConfigManager.Instance.ReadLanguageJson(configName);;		}		foreach (var item in config.datas)		{			if (item.ID == id)			{ 					return item;			}		}		return null;	}	public static List<DialogCfg> GetList()	{		if (config == null)		{			config= ConfigManager.Instance.Readjson<DialogCfg>(configName);		}		return config.datas;	}	public static string GetLangugeText(int id, Language language)	{		if (config == null)		{			config = ConfigManager.Instance.Readjson<DialogCfg>(configName);			languageConfigData = ConfigManager.Instance.ReadLanguageJson(configName);;		}		if (languageConfigData.languageItemDatas.ContainsKey(id))		{			foreach (var data in languageConfigData.languageItemDatas[id])			{				if (data.language == language)				return data.value;			}		}		return "";	}	}