using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class StartCardCfg  {	public int ID { get; set; }	public int[] Chapter { get; set; }	public string Args { get; set; }	 	public static string configName = "StartCardCfg";	public static StartCardCfg config { get; set; }	public static LanguageConfigData languageConfigData;	public string version { get; set; }	public List<StartCardCfg> datas { get; set; }	public static StartCardCfg Get(int id)	{		if (config == null)		{			config = ConfigManager.Instance.Readjson<StartCardCfg>(configName);			languageConfigData = ConfigManager.Instance.ReadLanguageJson(configName);;		}		foreach (var item in config.datas)		{			if (item.ID == id)			{ 					return item;			}		}		return null;	}	public static List<StartCardCfg> GetList()	{		if (config == null)		{			config= ConfigManager.Instance.Readjson<StartCardCfg>(configName);		}		return config.datas;	}	public static string GetLangugeText(int id, Language language)	{		if (config == null)		{			config = ConfigManager.Instance.Readjson<StartCardCfg>(configName);			languageConfigData = ConfigManager.Instance.ReadLanguageJson(configName);;		}		if (languageConfigData.languageItemDatas.ContainsKey(id))		{			foreach (var data in languageConfigData.languageItemDatas[id])			{				if (data.language == language)				return data.value;			}		}		return "";	}	}